package com.example.newsjam_android.ui.view.news

import android.content.Context
import android.util.Log
import androidx.fragment.app.activityViewModels
import androidx.fragment.app.viewModels
import androidx.lifecycle.Lifecycle
import androidx.lifecycle.lifecycleScope
import androidx.lifecycle.repeatOnLifecycle
import androidx.navigation.fragment.findNavController
import com.example.data.extensions.TokenManager
import com.example.data.model.ChatMessage
import com.example.data.model.NewsItem
import com.example.data.request.ChatBotDTO
import com.example.domain.mapper.toAI
import com.example.domain.mapper.toApp
import com.example.domain.mapper.toUser
import com.example.newsjam_android.GlobalApplication
import com.example.newsjam_android.R
import com.example.newsjam_android.data.enums.ChatOwner
import com.example.newsjam_android.databinding.FragmentNewsDescriptionBinding
import com.example.newsjam_android.ui.base.BaseFragment
import com.example.newsjam_android.ui.view.adapter.News4Adapter
import com.example.newsjam_android.ui.view.home.MainActivity
import com.example.newsjam_android.ui.view.listener.AdapterItemClickedListener
import com.example.newsjam_android.ui.view.viewmodel.ChatViewModel
import com.example.newsjam_android.ui.view.viewmodel.MainViewModel
import dagger.hilt.android.AndroidEntryPoint
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch
import javax.inject.Inject

@AndroidEntryPoint
class NewsDescriptionFragment :
    BaseFragment<FragmentNewsDescriptionBinding>(R.layout.fragment_news_description),
    AdapterItemClickedListener {
    private lateinit var news4Adapter: News4Adapter
    private val viewModel : ChatViewModel by activityViewModels()
    @Inject
    lateinit var tokenManager: TokenManager
    lateinit var activity: MainActivity

    override fun setLayout() {
        showFloating()
        initView()
        initAdapter()
    }

    private fun showFloating() {
        activity.showFloating()
    }

    override fun onStop() {
        super.onStop()
        activity.hideFloating()
        activity.initAdapter()
    }

    override fun onAttach(context: Context) {
        super.onAttach(context)
        activity = requireActivity() as MainActivity
    }


    private fun initView() {
        val newsItem = arguments?.getSerializable("NewsItem") as? NewsItem
        if (newsItem != null) {
            binding.newsData = newsItem
            newsItem.profile?.let { profile ->
                GlobalApplication.loadCropImage(
                    requireContext(),
                    binding.newsTextViewIv,
                    profile
                )
            }
            initChatBot(newsItem)
        } else {
            Log.e("NewsDescriptionFragment", "newsItem is null")
        }
    }


    private fun initChatBot(newsItem: NewsItem) {
        lifecycleScope.launch {
            activity.stateChangeChatType(chatOwner = ChatOwner.APP)
            if (newsItem.content.isNotEmpty()) {
                viewModel.postChatBot(
                    ChatBotDTO(
                        chat_message = "${newsItem.content}를 읽고 이 기사에 대해 자동 생성 질문을 의문형으로 1개 25자 이내로 만들어 줘",
                        news_id = newsItem.id
                    )
                )
                viewModel.postChatBot(
                    ChatBotDTO(
                        chat_message = "${newsItem.content}를 읽고 이 기사에 대해 자동 생성 질문을 의문형으로 1개 25자 이내로 만들어 줘",
                        news_id = newsItem.id
                    )
                )
            } else {
                Log.e("NewsDescriptionFragment", "newsItem content or id is null")
            }
        }
    }


    private fun initAdapter() {
        val list = listOf(
            NewsItem(
                profile = "data:image/jpeg;base64,"
            ),
            NewsItem(),
            NewsItem(),
            NewsItem(),
            NewsItem(),
            NewsItem()
        )
        news4Adapter = News4Adapter(this)
        binding.fragmentNewsDescriptionRv.adapter = news4Adapter
        news4Adapter.submitList(list)
    }

    override fun onClick(item: Any) {
        findNavController().navigate(R.id.action_newsDescriptionFragment_self)
    }


}